import React, {useEffect, useState} from 'react';
import styled from "styled-components"
import { useNavigate} from "react-router-dom";
import loader from "../assets/loader.gif"
import {ToastContainer, toast} from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import axios from "axios";
import {setAvatarRoute} from "../utils/APIRoutes";
import {Buffer} from "buffer";


export default function SetAvatar() {

    const api = 'https://api.multiavatar.com/45678945';
    const navigate = useNavigate();
    const [avatars,setAvatars] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [selectedAvatar, setSelectedAvatar] = useState(undefined);
    const [file, setFile] = useState({ preview: '', data: '' });
    const [fileName, setFileName] = useState("");

    const toastOptions = {
        position: "bottom-right",
        autoClose: 8000,
        pauseOnHover: true,
        draggable: true,
        theme: "dark"
    };

    useEffect(()=> {
        if(!localStorage.getItem('chat-app-user')){
            navigate('/login')
        }
    },[])

    const handleChange = (e) => {
        const img = {
            preview: URL.createObjectURL(e.target.files[0]),
            data: e.target.files[0],
        }
        setFile(img)
        // setFile(e.target.files[0]);
        // setFileName(e.target.files[0].name);
    };

    const handleSubmit = async (e) => {
        e.preventDefault()
        const formData = new FormData();
        formData.append("file", file.data);
        formData.append("fileName", fileName);
        try {
            const res = await axios.post(
                "http://localhost:4000/image",
                formData
            );
            console.log(res);
        } catch (ex) {
            console.log(ex);
        }
    };

    const setUploadedProfilePicture = async() => {
        if(file.data === undefined) {
            toast.error("Please select an avatar", toastOptions)
        } else {
            const user = await JSON.parse(localStorage.getItem('chat-app-user'));
            const {data} = await axios.post("http://localhost:4000/image", {
                image: file.data
            });
            console.log(data)
            if(data.data.message === "File Uploaded") {
                user.isAvatarImageSet = true;
                user.avatarImage = data.image;
                localStorage.setItem("chat-app-user", JSON.stringify(user));
                navigate('/');
            } else {
                toast.error("Error setting avatar. Please try again", toastOptions)
            }
        }
    };


    const setProfilePicture = async() => {
        if(selectedAvatar === undefined) {
            toast.error("Please select an avatar", toastOptions)
        } else {
            const user = await JSON.parse(localStorage.getItem('chat-app-user'));
            const {data} = await axios.post(`${setAvatarRoute}/${user._id}`, {
                image: avatars[selectedAvatar]
            });

            if(data.isSet) {
                user.isAvatarImageSet = true;
                user.avatarImage = data.image;
                localStorage.setItem("chat-app-user", JSON.stringify(user));
                navigate('/');
            } else {
                toast.error("Error setting avatar. Please try again", toastOptions)
            }
        }
    };


    useEffect(() => {
        async function fetchData() {
            const data = [];
            for (let i = 0; i < 4; i++) {
                const image = await axios.get(`${api}/${Math.round(Math.random() * 1000)}`
                );
                const buffer = new Buffer(image.data);
                data.push(buffer.toString("base64"))
            }
            setAvatars(data);
            setIsLoading(false)

        }
        fetchData()

    },[])

    return (
        <>
            {
                isLoading ? ( <Container>
                    <img src={loader} alt="loader" className="loader"/>
                        <form onSubmit={handleSubmit}>
                            <input type="file" onChange={handleChange}/>
                            <button className='submit-btn'>Upload</button>
                        </form>
                </Container>
                ):(
            <Container>
                <div className='title-container'>
                    <h1>Pick an avatar as your profile picture</h1>
                </div>
                <div className="avatars">
                    {
                        avatars.map((avatar, index) => {
                            return (
                                <div key={index}
                                     className = {`avatar ${
                                         selectedAvatar === index ? "selected" : ""}`}
                                >
                                    <img src={`data: image/svg+xml; base64, ${avatar}`}
                                         alt="avatar"
                                         key={avatar}
                                    onClick={() => setSelectedAvatar(index)}
                                    />
                                </div>
                            )
                        })
                    }
                </div>
                <button className="submit-btn" onClick={setProfilePicture}>Set as Profile Picture</button>
                <div className='Uploading Avatar'>
                    {file.preview && <img src={file.preview} width='100' height='100' />}
                    <form onSubmit={handleSubmit}>
                        <input  type='file' name='file' onChange={handleChange}></input>
                        <button className="submit-btn" onClick={setUploadedProfilePicture} type='submit'>Submit</button>
                    </form>
                </div>
            </Container>
                    )}
            <ToastContainer />
        </>
    )
}

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  gap: 3rem;
  background-color: #131324;
  height: 100vh;
  width: 100vw;
  .loader {
    max-inline-size: 100%;
  }
  .title-container {
    h1 {
      color: white;
    }
  }
  .avatars {
    display: flex;
    gap: 2rem;
    .avatar {
      border: 0.4rem solid transparent;
      padding: 0.4rem;
      border-radius: 5rem;
      display: flex;
      justify-content: center;
      align-items: center;
      transition: 0.5s ease-in-out;
      img {
        height: 6rem;
        transition: 0.5s ease-in-out;
      }
    }
    .selected {
      border: 0.4rem solid #4e0eff;
    }
  }
  .submit-btn {
    background-color: #997af0;
    color: white;
    padding: 1rem 2rem;
    border: none;
    font-weight: bold;
    cursor: pointer;
    border-radius: 0.4rem;
    font-size: 1rem;
    text-transform: uppercase;
    &:hover {
      background-color: #4e0eff;
    }
  }`;
